<?phpnamespace Domain\Catalog\QueryBuilders;use Domain\Catalog\Models\Brand;use Illuminate\Database\Eloquent\Builder;/** * @method static Brand|BrandQueryBuilder query() */class BrandQueryBuilder extends Builder{    public function homePage(): BrandQueryBuilder    {        return $this->where('on_home_page', true)            ->orderBy('sorting')            ->limit(6);    }}