<?phpnamespace Auth\Actions;use Domain\Auth\Contracts\RegisterNewUserContract;use Domain\Auth\DTOs\NewUserDTO;use Illuminate\Foundation\Testing\RefreshDatabase;use Tests\TestCase;class RegisterNewUserActionTest extends TestCase{    use RefreshDatabase;    public function test_it_success_user_created(): void    {        $this->assertDatabaseMissing('users', [            'email' => 'test@cutcode.ru'        ]);        $action = app(RegisterNewUserContract::class);        $action(NewUserDTO::make('Test', 'test@cutcode.ru', '123456789'));        $this->assertDatabaseHas('users', [            'email' => 'test@cutcode.ru'        ]);    }}